import React, {Component} from 'react';
import Heading from './heading/Heading.js';
import Experience from './experience/Experience.js';
import Fun from './fun/Fun.js';
import Contact from './contact/Contact.js';

import P5Wrapper from 'react-p5-wrapper';
import sketch from './sketches/sketch';
import './App.css';

class App extends Component  {
  constructor(){
    console.log('%cHello there! ðŸ‘‹ Check out more of my code at github.com/rachaelcodes', 'background: mediumspringgreen;')
    super();
    this.width = window.innerWidth;
    this.height = window.innerHeight;

    this.positions = [];

    // for (var i = 0; i < 150; i ++) {
    //   let pos = [];
    //   pos.push(Math.floor(Math.random() * this.width));
    //   pos.push(Math.floor(Math.random() * this.height));

    //   this.positions.push(pos);
    // }

    this.state = {
      positions: this.positions,
      width: this.width,
      height: this.height,
    };

    this.setPositions(this.width, this.height);
  }

  setPositions(width, height) {
    let positions = [];

    for (var i = 0; i < 150; i ++) {
      let pos = [];
      pos.push(Math.floor(Math.random() * width));
      pos.push(Math.floor(Math.random() * height));

      positions.push(pos);
    }

    return positions;
  }


  // updateDimensions() {

  // }


  // componentDidMount(){
  //   console.log(window.innerWidth)
  // }

  render() {
    return (
      <div className="App">
        <div className="App-contents">
          <Heading />
          <Experience />
          <Fun />
          <Contact />
        </div>

        <div className="P5">
          <P5Wrapper sketch={sketch} positions={this.state.positions}></P5Wrapper>
        </div>
        <link href="https://fonts.googleapis.com/css?family=Josefin+Sans:400,700&display=swap" rel="stylesheet"></link>
        {/* <link href="https://fonts.googleapis.com/css?family=Nunito:400,800&display=swap" rel="stylesheet"></link> */}
      </div>
    );
  }

}

export default App;
